// Auto type animation
let typed = new Typed(".type-animation-header", {
    strings: ["Click to Change The Background Color"],
    typeSpeed: 150,
    backSpeed: 100,
    loop: true,
  });

function commonGenerator() {
    const common = Math.floor(Math.random() * 255 + 1);
    return common;
}
function generateRGB() {
    const red = commonGenerator();
    const green = commonGenerator();
    const blue = commonGenerator();
    const rgb = `rgb(${red},${green},${blue})`
    return rgb;
}
//Function for changing color and update input field
function commonEvent(inputId, func) {
    const container = document.getElementById('root');
    const color = func;
    container.style.backgroundColor = color;
    document.getElementById(inputId).value = color;
}
//Copy functionality
function copyText(inputId) {
    const getInputField = document.getElementById(inputId);
    //Select the text filed after clicking
    getInputField.select();
    getInputField.setSelectionRange(0, 99999); //Select for mobile
    //Copy the text inside the text field
    navigator.clipboard.writeText(getInputField.value);
}
//Converting RGB to HEX
function convertToHex() {
    const rgb = generateRGB();
    //Removing extra rgb and comma from rgb color
    const rgbArray = rgb.split('')
    const rgbNumber = rgbArray.filter(e => e >= 0 && e <= 9);
    //Joining the filtered number
    const joinRgbNumber = rgbNumber.join('');
    const toNum = Number(joinRgbNumber)
    const hexCode = `#${toNum.toString(16)}`;
    return hexCode;
}
//On reload event
window.onload = () => {
    commonEvent('colorCodeRGB', generateRGB());
    commonEvent('colorCodeHEX', convertToHex());
}
//On click event
document.getElementById('change-btn').addEventListener('click', () => {
    commonEvent('colorCodeRGB', generateRGB());
    commonEvent('colorCodeHEX', convertToHex());
})

//Sound
function beep() {
    let snd = new  Audio("data:audio/wav;base64,");  
    snd.play();
}
const button = Array.from(document.getElementsByTagName('i'))
button.forEach(e => {
    e.addEventListener('click', beep)
})
//Copy Popup
const copyBtn = Array.from(document.querySelectorAll('.fa-copy'));
copyBtn.forEach(e => {
    e.addEventListener('click', () => {
        const copyPopup = document.querySelector('#popupId');
        const copyPopupH1 = document.querySelector('#popupHId');
        copyPopup.classList.add('popup')
        copyPopupH1.classList.add('popupH1')
    });
    e.addEventListener('click', () => {
        setTimeout(() => {
            const copyPopup = document.querySelector('#popupId');
            const copyPopupH1 = document.querySelector('#popupHId');
            copyPopup.classList.remove('popup');
            copyPopupH1.classList.remove('popupH1');
        }, 2000)
    })
})